    const { round } = roundInformation
    const onDeckEl = useRef(null)

    useEffect(() => {
        let onDeck = round.getOnDeck()
        onDeckEl.current = onDeck
        console.log(onDeckEl)
    }, [round])

    return(
        <div>

            <div ref={onDeckEl}>

            </div>

        </div>
    )
}

export default Scoring

