import { useState, useEffect, useRef } from 'react'
import { useLocalState } from './LocalState'

const Timer = () => {

    const [minutes, setMinutes] = useState(1)
    const [seconds, setSeconds] = useState(0)
    const [isActive, setIsActive] = useState(false)
    const roundStarted = useRef()
    const [cycles, setCycles] = useState(0)
    const { athletes,
            inIso, setInIso,
            onDeck, setOnDeck,
            climbing, setClimbing,
            finished, setFinished,
            nextIndex, setNextIndex    
    } = useLocalState()

    //updates climbers after they are done climbing 1 boulder
    function updateClimbers(currentClimbing) {
        const updatedClimbers = []
        let finishedIndex = 0;
        for(let i = 0; i < currentClimbing.length; i++) {
            if(currentClimbing[i].getLastBoulder() == true) {
                setFinished(finished => [...finished, currentClimbing[i]])
                finsihedIndex = i
            }  
            //update the boulders climbed value
            currentClimbing[i].cycle()
            updatedClimbers.push(currentClimbing[i])
        }
        //remove finsihed climber from array
        updatedClimbers.splice(finishedIndex, 1)
        return updatedClimbers
    }


    function addClimberToOnDeck() {
        const nextOnDeck = getNextClimber()
        setOnDeck(onDeck => [...onDeck, nextOnDeck])
    }

    function moveOnDeckToClimbing(next) {
        setClimbing(next)
    }

    function moveClimbingToOnDeck(updatedClimbers) {
        if(updatedClimbers.length == 0) return
        setOnDeck(updatedClimbers)
    }

    function cycleClimbers () {
        //store current arrays
        const currentOnDeck = onDeck;
        const currentClimbing = climbing;
        //reset state
        setOnDeck(null)
        setClimbing(null)
        //move on deck to climbing
        moveOnDeckToClimbing(currentOnDeck)
        //move current climbers to ondeck
        const updatedClimbers = updateClimbers(currentClimbing)
        moveClimbingToOnDeck(updatedClimbers)
        addClimberToOnDeck()
        timerReset()
    }

    function getNextClimber() {
        let currentIso = inIso
        const nextClimber = currentIso[0]
        const updatedIso = currentIso.splice(0, 1)
        setInIso(updatedIso)
        return nextClimber
    }

    useEffect(() => {
        let nextClimber = getNextClimber()
        console.log(nextClimber)
    }, [cycles])

    function startRound() {
        roundStarted.current = true;
        setInIso(athletes)
    }

    function toggle() {
        if(!isActive) {
            if(roundStarted.current == undefined) {
                startRound()
            }
        }
        setIsActive(!isActive)
    }

    function timerReset() {
        setMinutes(1)
        setSeconds(0)
        setCycles(cycles => cycles + 1)
    }

    //timer control
    useEffect(() => {
        if(isActive) {
            if(seconds == 0) {
                if(minutes == 0) {
                    timerReset()
                }
                if(minutes > 0) {
                    setSeconds(10)
                    setMinutes(minutes - 1)
                }
            }
            if(seconds > 0) {
                setTimeout(() => setSeconds(seconds - 1), 1000)
            }
        }
    }, [isActive, seconds, minutes])

    return (
    <div className='timer'>
        <h1>{minutes} : {seconds < 10 ? `0${seconds}` : seconds}</h1>
        <button onClick={toggle}>{isActive ? 'Stop timer' : 'Start timer'}</button>
    </div>
    )
}

export default Timer
