import { useLocalState } from './LocalState'

import ScoreCard from "./ScoreCard"

const Scoring = ({ activeClimbers }) => {

    const { roundInformation } = useLocalState()
    const { round } = roundInformation;
    console.log(round)
    return(
        <div>
            <h1>ON Deck</h1>
            {
                round.athletes.map(athlete => {
                    <p>{athlete.name}</p>
                })
            }
            <h1>Climbing</h1>
            {
                round.climbing.map(athlete => {
                    <p>{athlete.name}</p>
                })
            }
        </div>
    )
}

export default Scoring

