export default function Round (athletes) {
    this.athletes = athletes;
    this.activeTime = 4;
    this.restTime = 4;
    this.stacks = [];
    this.numBoulders = 4;
    this.onDeck = [];
    this.inIso = [];
    this.climbing = [];
    this.finished = [];
    this.roundStarted = false;
    this.athletesLeft = athletes.length
}

Round.prototype.startRound = function () {
    this.roundStarted = true;
    this.inIso.push(...this.athletes)
    this.onDeck.push(this.athletes[0])
    this.inIso.shift()
}

Round.prototype.cycle = function () {
    let tempClimbing = []
    //check if an athlete is done, remove from climbing arr and push to finished arr
    this.climbing.map((athlete, i) => {
        if(athlete.lastBoulder == true) {
            this.finished.push(athlete);
            this.climbing.splice(i, 1)
    }})
    //store on deck in temp arr
    tempClimbing.push(...this.onDeck)
    //push climbing arr to ondeck
    this.onDeck = []
    this.onDeck.push(...this.climbing)
    this.climbing.push(...tempClimbing)
}
