import { useEffect, useRef } from 'react'

import ScoreCard from "./ScoreCard"

const Scoring = ({ activeClimbers }) => {

    const { roundInformation } = useLocalState()
    const { round } = roundInformation
    let onDeck = round.getOnDeck()

    useEffect(() => {
        console.log(round)
    }, [round])

    return(
        <div>

            <p>{onDeck[0].name}</p>

        </div>
    )
}

export default Scoring

