import { useState } from 'react'

import Timer from '../components/timer'
import CreateCustomRound from '../components/CreateCustomRound'
import RoundSelector from '../components/RoundSelector'
import Button from '../components/Button'
import rounds from '../src/rounds'

const HomePage = () => {

    function handleClick(e) {
        e.preventDefault()
        alert('button clicked')
    }

    const [data, setData] = useState(initialData)
    return (
        <div className='container'>
            <div className='side-bar'>
                <div onClick={handleClick}>
                    <Button text='USAC Modified Redpoint'> </Button>
                </div>
                <Button text='3 Boulder Onsight'> </Button>
                <Button text='USAC Boulder Onsight' query='4BoulderOnsight'> </Button>
                <Button text='USAC Regionals' ></Button>
                <Button text='IFSC Boulder Qualification' ></Button>
                <Button text='USAC Sport Local Onsight' ></Button>
                <Button text='USAC Sport Regionals' ></Button>
                <Button text='USAC Sport Divisionals' ></Button>
                <Button text='Custom Timer' ></Button>
            </div>
            <div className='round-settings'>
                <h3>Round Type</h3>
                <div className='round-details'>
                    <div className='item'>
                        <button>+</button>
                        <span>Number of boulders: {data.numBoulders}</span>
                        <button>-</button>
                    </div>
                    <div className='item'>
                        <button>+</button>
                        <span>Active Time: {data.activeTime}</span>
                        <button>-</button>
                    </div>
                    <div className='item'>
                        <button>+</button>
                        <span>Rest Time: {data.restTime}</span>
                        <button>-</button>
                    </div>
                    <div className='item'>
                        <button>+</button>
                        <span>Number of athletes: {data.numAthletes}</span>
                        <button>-</button>
                    </div>
                </div>
            </div>
        <style global jsx>{`
            body {
                background-color: black;
                margin: 0 auto;
            }
        `}</style>
        <style jsx>{`
            .container {
                display: flex;
                flex-wrap: wrap;
            }
            .side-bar {
                width: 20%;
                height: 100vh;
                background-color: white;
                display: flex;
                flex-direction: column;
                align-items: center;
            }
            .round-settings {
                display: flex;
                flex-direction: column;
                background-color: white;
                width: 80%;
                height: 100vh;
                align-items: center;
            }
        `}</style>
        </div>
    )
}

const initialData = {
    title: 'USAC Boulder Onsight',
    numBoulders: 4,
    activeTime: 4,
    restTime: 4,
    numAthletes: 10
}

export default HomePage
