import { useState, useEffect } from 'react'

import { useLocalState } from '../components/LocalState'
import ScoreCard from '../components/ScoreCard'

const TimerPage = () => {
    const [minutes, setMinutes] = useState(4)
    const [seconds, setSeconds] = useState(0)
    const [isActive, setIsActive] = useState(false)
    const { roundInformation } = useLocalState()
    const [activeClimbers, setActiveClimbers] = useState([])
    const [onDeck, setOnDeck] = useState([])
    const [nextOnDeck, setNextOnDeck] = useState(0)

    function toggle() {
        setIsActive(!isActive)
    }

    function reset() {
        setMinutes(4)
        setSeconds(0)
        setNextOnDeck(nextOnDeck + 1)
    }

    function startRound() {
        setOnDeck(roundInformation.athletes[0])
        setNextOnDeck(nextOnDeck + 1)
    }
    //timer control
    useEffect(() => {
        if(isActive) {
            if(seconds == 0) {
                if(minutes > 0) {
                    setSeconds(59)
                    setMinutes(minutes - 1)
                }
                if(minutes == 0) {
                    reset()
                }
            }
            if(seconds > 0) {
                setTimeout(() => setSeconds(seconds - 1), 1000)
            }
        }
    }, [isActive, seconds, minutes])

    return (
        <div className='container'>
            {/* <div className='athlete-list'>
                <div className='athlete-list-item'>
                    <p>Name</p>
                    <p>Age</p>
                </div>
                <div className='athlete-list-item'>
                    <p>Name</p>
                    <p>Age</p>
                </div>
                <div className='athlete-list-item'>
                    <p>Name</p>
                    <p>Age</p>
                </div>
                <div className='athlete-list-item'>
                    <p>Name</p>
                    <p>Age</p>
                </div>
                <div className='athlete-list-item'>
                    <p>Name</p>
                    <p>Age</p>
                </div>
                <div className='athlete-list-item'>
                    <p>Name</p>
                    <p>Age</p>
                </div>
            </div> */}
            <div className='timer-and-scoring'>
                <div className='timer'>
                    <h1>{minutes} : {seconds < 10 ? `0${seconds}` : seconds}</h1>
                    <button onClick={toggle}>{isActive ? 'Stop' : 'Start'}</button>
                </div>
                <div className='scoring'>
                    <ScoreCard />
                    <ScoreCard />
                    <ScoreCard />
                    <ScoreCard />
                </div>
            </div>
            <style jsx>
                {`
                    .athlete-list {
                        width: 20%;
                        height: 100vh;
                        border: 1px solid black;
                    }
                    .timer-and-scoring {
                        display: flex;
                        flex-direction: column;
                    }
                    .timer {
                        display: flex;
                        flex-direction: column;
                        justify-content: flex-start;
                        align-items: center;
                    }
                    .container {
                        display: flex;
                    }
                    .scoring {
                        display: flex;
                        justify-content: space-around;
                    }
                    .score-card {
                        padding: 10px 10px;
                        border: 1px solid black;
                        width: 20vw;
                        display: flex;
                        align-items: center;
                        flex-direction: column;
                    }

                `}
            </style>
        </div>
    )
}

export default TimerPage
