    const [roundInformation, setRoundInformation] = useState({
        athletes: [{"name":"Sam Roehrich","ageCat":"JR"},{"name":"Brady Aspinwall","ageCat":"B"},{"name":"Izabella Adkins","ageCat":"JR"},{"name":"Teague Hendrix","ageCat":"JR"},{"name":"Laurel Falk","ageCat":"JR"},{"name":"Sadie Brown","ageCat":"B"},{"name":"Brodie Hendrix","ageCat":"A"},{"name":"Molly Bonine","ageCat":"A"},{"name":"Julia Marcus","ageCat":"C"},{"name":"Rolan Zaugg","ageCat":"JR"},{"name":"Josh Litten","ageCat":"JR"}]
    })

    function setLocalStateRoundInformation(roundInfo) {
        setRoundInformation(roundInfo)
    }

    const defaultValues = {
        roundInformation,
        setLocalStateRoundInformation
    }

    return <LocalStateProvider value={defaultValues}>
                {children}
            </LocalStateProvider>
}

function useLocalState() {
    const all = useContext(LocalStateContext)
    return all
}

